import React from 'react';
import ReactDom from 'react-dom'
import './index.css';

let test = require('~/ItemList.json');
console.log(test);

const Item = (props) => {

  const clickItem = (e) => {
    console.log(e);
    e.target.style.border = "3px solid #FF0000";
    alert("You are clicking the item: " + props.name);
  }

  return (
    <React.Fragment>
      <article className='item' onClick={clickItem}>
        <ItemImage img={props.img}/>
        <ItemName name={props.name}/>
        {props.children}
      </article>
    </React.Fragment>
  )
}

const ItemImage = (props) => {
  return (
    <img src={props.img} alt="png" width="50" height="50"/>
  )
}

const ItemName = (props) => {
  return (
    <h2 style={{color: '#617d98'}}>
    {props.name}
    </h2>
  )
}

const items = [
  {
    id: 0,
    itemName: "Coal",
    img: "https://static.wikia.nocookie.net/minecraft_gamepedia/images/5/58/Coal_JE4_BE3.png"
  },
  {
    id: 1,
    itemName: "Iron",
    img: "https://static.wikia.nocookie.net/minecraft_gamepedia/images/f/fc/Iron_Ingot_JE3_BE2.png"
  },
  {
    id: 2,
    itemName: "Gold",
    img: "https://static.wikia.nocookie.net/minecraft_gamepedia/images/8/8a/Gold_Ingot_JE4_BE2.png"
  }
]

const JSXItems = items.map((item) => {
  return (
    <Item key={item.id} name={item.itemName} img={item.img}/>
  );
})

// stateless functional component (must captialize the first character)
function App() {
  // return JSX/Html
  return (
    <React.Fragment>
      <h4>Title</h4>
      <section className='itemlist'>
        {JSXItems}
      </section>
    </React.Fragment>
  );
}

ReactDom.render(<App/>, document.getElementById('root'));
