import React from 'react';
import ReactDom from 'react-dom'
import './index.css';

const itemName = ["Iron", "Gold"];
const img = ["https://static.wikia.nocookie.net/minecraft_gamepedia/images/f/fc/Iron_Ingot_JE3_BE2.png", "https://static.wikia.nocookie.net/minecraft_gamepedia/images/8/8a/Gold_Ingot_JE4_BE2.png"];
// stateless functional component (must captialize the first character)
function App() {
  // return JSX/Html
  return (
    <React.Fragment>
      <h4>Title</h4>
      <section className='itemlist'>
        <Item name={itemName[0]} img={img[0]}>
          <p>
          Remark: this is how you add additional information for a specific item. (Access with props.children)
          </p>
        </Item>
        <Item name={itemName[1]} img={img[1]}/>
      </section>
    </React.Fragment>
  );
}

const Item = (props) => {
  return (
    <React.Fragment>
      <article className='item'>
        <ItemImage img={props.img}/>
        <ItemName name={props.name}/>
        {props.children}
      </article>
    </React.Fragment>
  )
}

const ItemImage = (props) => {
  return (
    <img src={props.img} alt="png" width="50" height="50"/>
  )
}

const ItemName = (props) => {
  return (
    <h2 style={{color: '#617d98'}}>
    {props.name}
    </h2>
  )
}

ReactDom.render(<App/>, document.getElementById('root'));
